    /* ================== GET ================== */

    @GET
    @Produces(MediaType.APPLICATION_JSON)
    public Quest hello() {
        return new Quest();
    }

    @GET
    @Produces(MediaType.APPLICATION_JSON)
    @Path("/all")
    public Iterable<Quest> getAll() {
        return questRepository.findAll();
    }

    @GET
    @Produces(MediaType.APPLICATION_JSON)
    @Path("/name")
    public Iterable<Quest> getByName(@QueryParam("name") String name) {
        return questRepository.findByName(name);
    }

    @GET
    @Produces(MediaType.APPLICATION_JSON)
    @Path("/location")
    public Iterable<Quest> getByLocation(@QueryParam("location") String location) {
        return questRepository.findByLocation(location);
    }


    /* ================== POST ================== */

    @POST
    @Produces(MediaType.APPLICATION_JSON)
    public Quest insert(Quest quest) {

        //Préféré faire un by name pour l'initilisation au débpart si les ID ne sont pas les mêmes
        Optional<Succes> succes = succesController.getByName(quest.getSucces().getName());
        if(succes.isPresent()) {
            quest.setSucces(succes.get());
            questRepository.save(quest);
            return quest;
        }
        else {
            throw new IllegalArgumentException("Le succes au nom " + quest.getSucces().getName() + " n'existe pas");
        }

    }


    /* ================== DELETE ================== */

    @DELETE
    @Produces(MediaType.APPLICATION_JSON)
    public String deleteById(@QueryParam("id") long id) {
        questRepository.deleteById(id);
        return "deleted quest with id " + id + " done.";
    }


